     // æ¶‰åŠå·¦è¾¹å°±å»å·¦è¾¹        if (r &gt; mid)  update(id &lt;&lt; 1 | 1, l, r, val);  // æ¶‰åŠå³è¾¹å°±å»å³è¾¹        // å›æº¯ï¼šæ›´æ–°çˆ¶èŠ‚ç‚¹çš„å€¼ (Push Up)        a[id].v = a[id &lt;&lt; 1].v + a[id &lt;&lt; 1 | 1].v;    &#125;    //4ï¼ŒåŒºé—´æŸ¥è¯¢        long long query(int id, int l, int r) &#123;//è¿™ä¸ªå‡½æ•°çš„åŠŸèƒ½æ˜¯ï¼Œä»ä¸‹æ ‡ä¸ºidçš„ç»“ç‚¹å‡ºå‘ï¼ŒæŸ¥è¯¢åŒºé—´[l,r]çš„æ€»å’Œ        int L = a[id].l;        int R = a[id].r;        if (l &lt;= L &amp;&amp; R &lt;= r) &#123;            return a[id].v;        &#125;        // ã€å…³é”®ã€‘ï¼šæŸ¥è¯¢ä¹‹å‰ä¹Ÿè¦æ¨æ ‡è®°        push_down(id);        int mid = (L + R) &gt;&gt; 1;        long long res = 0;        if (l &lt;= mid) res += query(id &lt;&lt; 1, l, r);//è¿™ä¸ªåœ°æ–¹é€šè¿‡ç´¯åŠ å®ç°åŒºé—´çš„åˆ†å‰²        if (r &gt; mid)  res += query(id &lt;&lt; 1 | 1, l, r);        return res;&#125;

